mapper.update:
UPDATE ctg_guide
SET   ctg_guide_title = #{ctg_guide_title}
  , ctg_guide_cont = #{ctg_guide_cont}
  , mod_dtm = now()
  ,  mod_id = #{mod_id}
WHERE ctg_guide_seq = #{ctg_guide_seq} and reg_id = #{reg_id}

UPDATE ctg_guide
SET ctg_guide_view_cnt = ctg_guide_view_cnt + 1
WHERE ctg_guide_seq = #{ctg_guide_seq}
