mapper.update:
UPDATE news
SET   news_title = #{news_title}
  , news_cont = #{news_cont}
  , mod_dtm = now()
  ,  mod_id = #{reg_id}
WHERE news_seq = #{news_seq} and reg_id = #{reg_id}

UPDATE news
SET news_view_cnt = news_view_cnt + 1
WHERE news_seq = #{news_seq}

mapper.select:
SELECT * FROM news
WHERE news_title LIKE #{news_title}
ORDER BY reg_dtm DESC
