mapper.select:
select *
from SEL_BUY_MSG
where user_id = #{user_id}
and pj_id = #{pj_id}

mapper.insert:
insert into SEL_BUY_MSG(user_id, pj_id, dba_reg_id) values (#{user_id} , #{pj_id}, #{user_id})
-- 채팅방 생성과 동시에 ChatRequest 객체에 생성된 채팅방을 담는다.
select * from SEL_BUY_MSG where user_id = #{user_id} and pj_id = #{pj_id}
