mapper.select:
select like_status from LIKE_PJ
where user_id = #{user_id}
and pj_id = #{pj_id}

mapper.update:
update from LIKE_PJ
set like_status = 0
where user_id = #{user_id}
and pj_id = #{pj_id}

update from LIKE_PJ
set like_status = 1
where user_id = #{user_id}
and pj_id = #{pj_id}

mapper.delete:
delete from LIKE_PJ
where user_id = #{user_id}
and pj_id = #{pj_id}
