mapper.select:
select count(*) from LIKE_PJ
where user_id = #{user_id}
and like_status = 1

select like_status from LIKE_PJ
where user_id = #{user_id}
and pj_id = #{pj_id}

mapper.update:
update LIKE_PJ
set like_status = 0
where user_id = #{user_id}
and pj_id = #{pj_id}

update LIKE_PJ
set like_status = 1, like_dtm = NOW()
where user_id = #{user_id}
and pj_id = #{pj_id}

mapper.select:
select a.*,b.*
from pj a , like_pj b
where a.pj_id = #{pj_id}
  and b.user_id = #{user_id}

mapper.delete:
delete from LIKE_PJ
where user_id = #{user_id}
and pj_id = #{pj_id}
